function autoinitre(){
$("#fk-top-search-box").autocomplete("/s", {
width: 346,
max: 12,
minChars:2,
scroll: false,
selectFirst: false,
formatItem:formatItem
});
$("#fk-mI li").click(function(){
if($("#fk-top-search-box").val()=="Search for items") {
$("#fk-top-search-box").val("");
}
$("#fk-top-search-box").focus();
$("#fk-top-search-box").css({"border-color":"#005576", "color":"#333333"});
});
$("#fk-top-search-box").keypress(function(e){
if(e.keyCode==13 && $(this).val() == "" && $("#fk-vertical-auto-suggestions").val()!="all") {
var vertical = $("#fk-vertical-auto-suggestions").val();
window.location.href = searchOptions_urls[vertical];
return;
}
});
}
function formatItem(row) {
return row.suggestion;
}

